package com.example.mpproject;import android.content.Context;import android.graphics.Canvas;import android.graphics.Color;import android.graphics.Paint;import android.util.AttributeSet;import android.view.View;public class CustomProgress extends View {    private Paint paint;    private final int radiusMainArc = 300;    private final int fullSteps = 10000;    private int currentProgress;    public CustomProgress(Context context, AttributeSet attrs, int defStyle) {        super(context, attrs, defStyle);        init();    }    public CustomProgress(Context context, AttributeSet attrs) {        super(context, attrs);        init();    }    public CustomProgress(Context context) {        super(context);        init();    }    private void init() {        paint = new Paint();        paint.setAntiAlias(true);    }    public void setCurrentProgress(int steps) {        currentProgress = Math.max(0, Math.min(steps, fullSteps));        invalidate();    }    @Override    protected void onDraw(Canvas canvas) {        super.onDraw(canvas);        drawMainArc(canvas);        cursor(canvas);    }    private void drawMainArc(Canvas canvas) {        float x = getWidth() / 2f;        float y = getHeight() / 2f;        paint.setStyle(Paint.Style.STROKE);        paint.setStrokeWidth(90f);        paint.setColor(Color.parseColor("#FFFFFF"));        canvas.drawArc(                x - radiusMainArc, y - radiusMainArc, x + radiusMainArc, y + radiusMainArc, 150f, 264f, false,                paint        );    }    private void cursor(Canvas canvas) {        float x = getWidth() / 2f;        float y = getHeight() / 2f;        paint.setStyle(Paint.Style.STROKE);        paint.setStrokeWidth(90f);        paint.setColor(Color.parseColor("#14213D"));        float currentAngle = currentProgress * 264f / fullSteps;        canvas.drawArc(                x - radiusMainArc, y - radiusMainArc, x + radiusMainArc, y + radiusMainArc, 123f, currentAngle, false,                paint        );        float xx = x + (radiusMainArc) * (float) Math.cos(237f * (float) Math.PI / 180f - currentAngle * (float) Math.PI / 180f);        float yy = y - (radiusMainArc) * (float) Math.sin(237f * (float) Math.PI / 180f - currentAngle * (float) Math.PI / 180f);        paint.setStyle(Paint.Style.FILL);        paint.setStrokeWidth(0f);        canvas.drawCircle(xx, yy, 45f, paint);    }}